const data = JSON.parse(items[0].json.choices[0].message.content);
const slug = data.slug;
const full = `<!doctype html><html lang="en"><head><meta charset="utf-8"/><meta name="viewport" content="width=device-width,initial-scale=1"/><title>${data.title} — LearnPulse</title><meta name="description" content="${data.metaDescription}"/></head><body>${data.bodyHtml}</body></html>`;
return [{ json: { slug, title: data.title, html: full } }];
